    case "WS0080":
        msg.payload.Warning="Warning Garage Side Door is Open";
        break;
    case "TS0105":
        msg.payload.Warning="Warning Office Door Is Open";
        break;

    default :
        msg.payload.Warning=StationID;
        break;
}
}
return msg;
function getSecSinceMid(d) {
  var e = new Date(d);
  return Math.floor((d - e.setHours(0,0,0,0))/1000);
}

